import { Directive, ElementRef, EventEmitter, OnDestroy, Output } from '@angular/core';
import { debounceTime, fromEvent } from 'rxjs';
import { SubSink } from 'subsink';
import { WebEvent } from '../events';

@Directive({
  standalone: true,
  selector: '[sharedClickDebounce]',
})
export class ClickDebounceDirective implements OnDestroy {
  // TODO: 
  @Output() monorepoClickDebounce = new EventEmitter();
  private subSink = new SubSink();

  constructor(elemRef: ElementRef) {
    // pointEvent, MouseEventHostListener
    this.subSink.sink = fromEvent(elemRef.nativeElement, WebEvent.Mouse.Type.Click)
      .pipe(debounceTime(300))
      .subscribe((x) => this.monorepoClickDebounce.emit(x));
  }

  ngOnDestroy() {
    this.subSink.unsubscribe();
  }
}

import { Directive, EventEmitter, HostListener, OnDestroy, Output } from '@angular/core';
import { debounceTime, Subject } from 'rxjs';
import { SubSink } from 'subsink';

@Directive({
  standalone: true,
  selector: '[sharedClickDebounceStopPropagation]',
})
export class ClickDebounceStopPropagationDirective implements OnDestroy {
  // TODO: 
  @Output() clickDebounceStopPropagation = new EventEmitter();
  private subSink = new SubSink();
  private click$ = new Subject<unknown>();

  constructor() {
    this.subSink.sink = this.click$
      .pipe(debounceTime(300))
      .subscribe((x) => this.clickDebounceStopPropagation.emit(x));
  }

  @HostListener('click', ['$event'])
  clickEvent(event: MouseEvent) {
    event.preventDefault();
    event.stopPropagation();
    this.click$.next(event);
  }

  ngOnDestroy() {
    this.subSink.unsubscribe();
  }
}

