import { CommonModule } from '@angular/common';
import { Component, computed, inject, input, output, signal } from '@angular/core';
import { CheckboxComponent, WebButtonComponent } from 'web/uis';
import { TaskEntity, TodolistSignalStore } from '../../stores';
@Component({
  selector: 'lib-task-component',
  standalone: true,
  imports: [CommonModule, WebButtonComponent, CheckboxComponent],
  templateUrl: './task-component.component.html',
})
export class TaskComponentComponent {
  public updateTask = output<TaskEntity>();
  public task = input.required<TaskEntity>();
  public isEditing = signal(false);
  protected store = inject(TodolistSignalStore);
  protected selected = computed(() => this.store.selectedIds().includes(this.task().id));
  onUpdateTask() {
    this.updateTask.emit(this.task());
  }
  public onDeleteTask() {
    this.store.deleteTodo(this.task());
  }

  enableEdit() {
    this.isEditing.set(true)
  }

  saveEdit() {
    this.isEditing.set(false)
    this.updateTask.emit(this.task()); // Emit the updated task back to the parent
  }

  public selectChanged(isSelected: boolean) {
    if (isSelected) {
      this.store.addSelected(this.task().id);
      return;
    }
    this.store.removeSelected(this.task().id);
  }

}

import { CommonModule } from '@angular/common';
import { Component, inject, model } from '@angular/core';
import { FormsModule } from '@angular/forms';
import * as uuid from 'uuid';
import { WebButtonComponent } from 'web/uis';
import { TodolistSignalStore } from '../../stores';
import { TodolistFooterComponent } from '../../uis/todolist-footer/todolist-footer.component';
import { TaskComponentComponent } from '../task-component/task-component.component';


@Component({
  selector: 'todolist',
  standalone: true,
  imports: [CommonModule, FormsModule, TaskComponentComponent, WebButtonComponent, TodolistFooterComponent],
  providers: [TodolistSignalStore],
  template: `<section class="mx-auto mt-10 max-w-lg rounded-md bg-white p-4 shadow-md">
  <header class="mb-4 text-2xl font-bold text-gray-700">Todo List</header>

  <!-- Add Task Section -->
  <section class="mb-4 flex items-center gap-2">
    <input
      #addTaskInput
      [(ngModel)]="taskModel"
      type="text"
      placeholder="Add a new task"
      class="flex-1 rounded-md border border-gray-300 px-4 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
      (keydown.enter)="test(addTaskInput)"
    />
    <uis-web-button [buttonName]="'Add Task'" (click)="addTaskHandler()"></uis-web-button>
  </section>

  <!-- Task List Section -->
  <section>
    @if (store.todoLists().length > 0) {
      <div class="space-y-2">
        @for (task of store.todoLists(); track $index) {
          <lib-task-component [task]="task"></lib-task-component>
        }
      </div>
    } @else {
      <p class="text-center text-gray-500">No tasks available. Add your first task!</p>
    }
  </section>

  <lib-todolist-footer
    [showCompletedArea]="!!store.selectedIds().length"
    (completeTodos)="completeHandler($event)"
  ></lib-todolist-footer>
</section>
`,
})
export class TodolistComponent {
  protected store = inject(TodolistSignalStore);
  protected taskModel = model<string>('');


  // Method 1: Model, pass Input from template
  protected test(inputElem: HTMLInputElement): void {
    if (inputElem.value.length === 0 || inputElem.value.trim() === '') {
      return;
    }
    this.store.addTodo({
      id: uuid.v4(),
      name: inputElem.value,
      completed: false,
      ts: new Date().getTime().toString()
    });
    inputElem.value = '';
    this.taskModel.set('');
  }

  // Method 2: Model, Model
  //  taskModel , angular 
  // this.taskModel.subscribe((value) => {
  //  console.log('TaskModel', value);
  // })
  protected addTaskHandler(): void {
    if (this.taskModel().length === 0 || this.taskModel().trim() === '') {
      return;
    }
    this.store.addTodo({
      id: uuid.v4(),
      name: this.taskModel(),
      completed: false,
      ts: new Date().getTime().toString()
    });
    this.taskModel.set('');
  }


  protected completeHandler(completed: boolean): void {
    this.store.completeAllTodos(completed);
  }
}

import { Component, input, output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { WebBorderButtonComponent, WebButtonComponent } from 'web/uis';

@Component({
  selector: 'lib-todolist-footer',
  standalone: true,
  imports: [CommonModule, WebBorderButtonComponent, WebButtonComponent],
  template: `<footer class="mt-4 flex justify-between text-sm">
  <uis-web-button
    [btnClasses]="'bg-gray-500 hover:bg-gray-600'"
